+import nl.utwente.di.interactief2.JDBC.attribute_queries.PersonAttributes;
+import nl.utwente.di.interactief2.JDBC.attribute_queries.TeamAttributes;
+import org.json.JSONObject;
+import org.junit.jupiter.api.Assertions;
+import org.junit.jupiter.api.BeforeEach;
+import org.junit.jupiter.api.Test;
+
+class PersonTest {
+
+    private PersonAttributes personAttributes;
+    @BeforeEach
+    void setup() {
+        personAttributes = new PersonAttributes();
+    }
+
+    @Test
+    void checkPasswordTest() {
+        Assertions.assertFalse(personAttributes.checkPassword(0, "not a password"));
+        Assertions.assertTrue(personAttributes.checkPassword(0, "password"));
+    }
+
+    @Test
+    void checkIfAdminTest() {
+        Assertions.assertTrue(personAttributes.checkIfAdmin(0));
+    }
+
+    @Test
+    void getParticipantByIDTest() {
+        JSONObject person;
+        person = personAttributes.getParticipantByID(0);
+        Assertions.assertNull(person);
+
+        person = personAttributes.getParticipantByID(1);
+        Assertions.assertEquals("Joep", person.getJSONObject(PersonAttributes.PARTICIPANT_OBJECT_NAME).get("name"));
+    }
+
+    @Test
+    void insertPersonAndGetPersonByIDAndDeletePersonCheck() {
+        //Insert a guy and make sure it didn't exist already.
+        JSONObject person;
+        Assertions.assertTrue(personAttributes.insertPerson(1000, "just_a_lil_guy", "-", "password"));
+
+        //Check if inserted guy can be found (which also checks if the getPersonByID method works)
+        person = personAttributes.getPersonByID(1000);
+        Assertions.assertEquals("just_a_lil_guy", person.getJSONObject("person").get("name"));
+
+        //Delete the guy and check if it exists.
+        personAttributes.deletePerson(1000, new TeamAttributes());
+        person = personAttributes.getPersonByID(1000);
+        Assertions.assertNull(person);
+    }
+
+}

