+package nl.utwente.di.interactief2.rest_api;
+
+import jakarta.ws.rs.Consumes;
+import jakarta.ws.rs.HeaderParam;
+import jakarta.ws.rs.POST;
+import jakarta.ws.rs.Path;
+import jakarta.ws.rs.core.Response;
+import nl.utwente.di.interactief2.webpush.Subscription;
+import nl.utwente.di.interactief2.webpush.WebPushService;
+import org.json.JSONObject;
+
+import java.util.ArrayList;
+import java.util.List;
+
+@Path("/notifications")
+public class Notifications {
+
+    private WebPushService webPushService;
+
+    private static List<Subscription> subscriptions = new ArrayList<>();
+
+    public Notifications() {
+        webPushService = new WebPushService();
+    }
+
+    @POST
+    @Path("/subscribe")
+    @Consumes("application/json")
+    public Response subscribe(String body) {
+        JSONObject json = new JSONObject(body);
+        Subscription subscription = new Subscription();
+        subscription.setEndpoint(json.getString("endpoint"));
+        subscription.setKey(json.getString("key"));
+        subscription.setAuth(json.getString("auth"));
+
+        subscriptions.add(subscription);
+
+        return Response.status(Response.Status.OK).build();
+    }
+
+    @POST
+    @Consumes("application/json")
+    public Response sendNotification(@HeaderParam("Authorization") String token, String body) {
+        JSONObject json = new JSONObject(body);
+        String message = json.getString("message");
+
+        for (Subscription subscription : subscriptions) {
+            webPushService.sendPushMessage(subscription, message);
+        }
+
+        return Response.status(Response.Status.OK).build();
+    }
+
+
+}

